import numpy as np

labels = {}

filename = 'labels1.txt'

# 读取文件内容
with open(filename, 'r') as f:
    lines = f.readlines()

# 解析每行并添加到字典中
for line in lines:
    # 去除行尾的换行符（如果有的话）
    line = line.strip()
    # 假设每行都是一个'key: value'的字符串
    if ':' in line:
        index = line.find(':')
        key = int(line[:index])
        value = line[index + 3:-2]

        try:
            key = int(key)
        except ValueError:
            pass  # 如果转换失败，key保持为字符串
        # 将键值对添加到字典中
        labels[key] = value.strip()  # 去除值两侧可能的多余空格

print(labels.keys())  # 这将显示字典中所有的键，您可以看到它们是整型还是字符串
print(labels.values())
print(labels)
